import React, { useState, useEffect, useRef } from 'react';
import DuplasList from './components/Duplas/DuplasList';
import FaseDeGrupos from './components/Torneio/FaseDeGrupos';
import FaseEliminatoria from './components/Torneio/FaseEliminatoria';
import Button from './components/UI/Button';
import Input from './components/UI/Input';
import './App.css';
import {
  dividirGrupos,
  classificarDuplasPorDesempate,
  criarJogosParaGrupo
} from './utils/torneioUtils';

function App() {
  const [duplas, setDuplas] = useState([]);
  const [faseAtual, setFaseAtual] = useState('duplas');
  const [grupos, setGrupos] = useState([]);
  const [jogosEliminatoria, setJogosEliminatoria] = useState([]);
  const [ranking, setRanking] = useState([]);
  const [historicoJogos, setHistoricoJogos] = useState([]);
  const [historicoFases, setHistoricoFases] = useState([]);

  useEffect(() => {
    console.log('Estado dos jogos eliminatórios atualizado:', jogosEliminatoria);
  }, [jogosEliminatoria]);

  const jogador1Ref = useRef(null);
  const jogador2Ref = useRef(null);

  const addDupla = (dupla) => {
    if (!duplas.some(d => d.includes(dupla[0]) || d.includes(dupla[1]))) {
      setDuplas(prevDuplas => [...prevDuplas, dupla]);
    } else {
      alert('Os nomes dos jogadores devem ser únicos. Um ou ambos já estão registrados.');
    }
  };

  const removeDupla = (index) => {
    setDuplas(duplas.filter((_, i) => i !== index));
  };

  const startTournament = () => {
    if (duplas.length < 3) {
      alert('É necessário pelo menos 3 duplas para começar o torneio.');
      return;
    }
    const gruposCriados = dividirGrupos([...duplas].sort(() => Math.random() - 0.5));
    gruposCriados.forEach(grupo => {
      grupo.jogos = criarJogosParaGrupo(grupo.duplas, grupo.id);
    });
    setGrupos(gruposCriados);
    setFaseAtual('grupos');
  };

  const iniciarFaseEliminatoria = (classificados) => {
    console.log('Classificados para a fase eliminatória:', classificados);
    let jogos = [];
    let duplasRestantes = classificados.length;
    let rodadas = Math.ceil(Math.log2(duplasRestantes));

    let currentIndex = 0;

    // Ajuste para cruzamento na semifinal se houver exatamente 4 duplas
    if (duplasRestantes === 4) {
      jogos.push({
        dupla1: classificados[0], // 1º do Grupo 1
        dupla2: classificados[3], // 2º do Grupo 2
        placar: '',
        submetido: false,
        jogoIndex: currentIndex++,
        rodada: 1,
        fase: 'Semifinal'
      });
      jogos.push({
        dupla1: classificados[2], // 2º do Grupo 1
        dupla2: classificados[1], // 1º do Grupo 2
        placar: '',
        submetido: false,
        jogoIndex: currentIndex++,
        rodada: 1,
        fase: 'Semifinal'
      });
    } else {
      // Para mais de 4 duplas ou para configurar rodadas adicionais
      for (let r = 1; r <= rodadas; r++) {
        let jogosDaRodada = [];
        for (let i = 0; i < duplasRestantes; i += 2) {
          let jogo = {
            dupla1: classificados[i] || ['TBD', ''],
            dupla2: classificados[i + 1] || ['TBD', ''],
            placar: '',
            submetido: false,
            jogoIndex: currentIndex++,
            rodada: r,
            fase: r === rodadas ? 'Final' : (r === rodadas - 1 ? 'Semifinal' : `Rodada ${r}`)
          };
          jogosDaRodada.push(jogo);
        }
        jogos.push(...jogosDaRodada);
        duplasRestantes = Math.ceil(duplasRestantes / 2);
        // Ajuste para a próxima rodada, movendo os vencedores para frente na lista
        classificados = classificados.filter((_, index) => index % 2 === 0);
      }
    }

    console.log('Jogos eliminatórios criados:', jogos);
    setJogosEliminatoria(jogos);
    setFaseAtual('eliminatória');
  };

  const encerrarFaseGrupos = () => {
    if (grupos.some(grupo => grupo.jogos.some(jogo => !jogo.submetido))) {
      alert('Todos os jogos devem ser submetidos antes de encerrar a fase de grupos.');
      return;
    }

    let classificados = classificarDuplasPorDesempate([...duplas], grupos.flatMap(grupo => grupo.jogos));
    setHistoricoFases(prevHistorico => [...prevHistorico, {
      fase: 'Fase de Grupos',
      resultados: grupos.map(grupo => ({
        grupoId: grupo.id,
        jogos: grupo.jogos
      })),
      classificados: classificados.slice(0, 2 * grupos.length) // assumindo 2 por grupo
    }]);

    if (duplas.length <= 5) {
      alert(`Campeão: ${classificados[0].join(' e ')}`);
      setFaseAtual('finalizado');
    } else {
      iniciarFaseEliminatoria(classificados);
    }
  };

  const encerrarFaseEliminatoria = () => {
    if (jogosEliminatoria.some(jogo => !jogo.submetido)) {
      alert('Todos os jogos devem ser submetidos antes de avançar para a próxima rodada.');
      return;
    }

    let ultimaRodada = Math.max(...jogosEliminatoria.map(jogo => jogo.rodada));
    let jogosUltimaRodada = jogosEliminatoria.filter(jogo => jogo.rodada === ultimaRodada);

    if (jogosUltimaRodada.length === 1) { // Final
      let final = jogosUltimaRodada[0];
      let vencedorFinal = final.placar.split('-').map(Number)[0] > final.placar.split('-').map(Number)[1] ? final.dupla1 : final.dupla2;
      let perdedorFinal = vencedorFinal === final.dupla1 ? final.dupla2 : final.dupla1;

      // Encontrar os perdedores das semifinais para a disputa do 3º lugar
      let semiFinais = jogosEliminatoria.filter(jogo => jogo.rodada === ultimaRodada - 1);
      let perdedoresSemifinal = semiFinais.map(jogo => {
        const [set1, set2] = jogo.placar.split('-').map(Number);
        return set1 > set2 ? jogo.dupla2 : jogo.dupla1;
      });

      // Adicionar jogo para determinar o 3º lugar
      setJogosEliminatoria(prevJogos => [
        ...prevJogos,
        {
          dupla1: perdedoresSemifinal[0],
          dupla2: perdedoresSemifinal[1],
          placar: '',
          submetido: false,
          jogoIndex: prevJogos.length,
          rodada: ultimaRodada + 1,
          fase: 'Disputa 3º Lugar'
        }
      ]);

      finalizarTorneio(vencedorFinal, perdedorFinal);
    } else {
      // Identificar vencedores da última rodada
      let vencedores = jogosUltimaRodada.map(jogo => {
        const [set1, set2] = jogo.placar.split('-').map(Number);
        return set1 > set2 ? jogo.dupla1 : jogo.dupla2;
      });

      // Adicionar nova rodada
      let novaRodada = ultimaRodada + 1;
      let novosJogos = [];
      for (let i = 0; i < vencedores.length; i += 2) {
        novosJogos.push({
          dupla1: vencedores[i],
          dupla2: vencedores[i + 1] || ['TBD', ''],
          placar: '',
          submetido: false,
          jogoIndex: jogosEliminatoria.length + (i / 2),
          rodada: novaRodada,
          fase: novaRodada === ultimaRodada + 1 ? 'Final' : `Rodada ${novaRodada}`
        });
      }

      setJogosEliminatoria(prevJogos => [...prevJogos, ...novosJogos]);
    }
  };

  const atualizarEstatisticas = (jogoInfo) => {
    console.log('jogoInfo recebido em atualizarEstatisticas:', jogoInfo);

    setJogosEliminatoria(prevJogos => {
      const updatedJogos = prevJogos.map(jogo =>
        jogo.jogoIndex === jogoInfo.jogoIndex ? { ...jogo, placar: jogoInfo.placar, submetido: true } : jogo
      );

      const jogoExiste = historicoJogos.some(historicoJogo =>
        historicoJogo.jogoIndex === jogoInfo.jogoIndex &&
        historicoJogo.grupo === jogoInfo.grupo
      );

      if (!jogoExiste) {
        setHistoricoJogos(prevHistorico => [...prevHistorico, { ...jogoInfo, grupo: jogoInfo.grupo }]);
      }

      return updatedJogos;
    });
  };

  const groupBy = (array, key) => {
    return array.reduce((rv, x) => {
      (rv[x[key]] = rv[x[key]] || []).push(x);
      return rv;
    }, {});
  };

  const finalizarTorneio = (vencedorFinal, perdedorFinal) => {
    let disputaTerceiroLugar = jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar');

    if (disputaTerceiroLugar && disputaTerceiroLugar.submetido) {
      let terceiroLugar = disputaTerceiroLugar.placar.split('-').map(Number)[0] > disputaTerceiroLugar.placar.split('-').map(Number)[1] ? disputaTerceiroLugar.dupla1 : disputaTerceiroLugar.dupla2;
      let quartoLugar = terceiroLugar === disputaTerceiroLugar.dupla1 ? disputaTerceiroLugar.dupla2 : disputaTerceiroLugar.dupla1;

      setRanking([
        { lugar: 1, dupla: vencedorFinal },
        { lugar: 2, dupla: perdedorFinal },
        { lugar: 3, dupla: terceiroLugar },
        { lugar: 4, dupla: quartoLugar }
      ]);

      setFaseAtual('finalizado');
    }
  };

  const add8Duplas = () => {
    let newDuplas = [];
    for (let i = 1; i <= 8; i++) {
      newDuplas.push([`JOGADOR${i * 2 - 1}`, `JOGADOR${i * 2}`]);
    }
    setDuplas(prevDuplas => [...prevDuplas, ...newDuplas]);
  };

  return (
    <div className="App">
      <header>
        <h1>BT Connect</h1>
      </header>
      <main className="container">
        <section className="section">
          <h2>Informações do Torneio</h2>
          <label htmlFor="nomeTorneio">Nome do Torneio:</label>
          <input type="text" id="nomeTorneio" placeholder="Digite o nome do torneio" required />
          <label htmlFor="dataTorneio">Dia do Torneio:</label>
          <input type="date" id="dataTorneio" required />
        </section>
        {faseAtual === 'duplas' && (
          <>
            <DuplasList duplas={duplas} onRemoveDupla={removeDupla} />
            <div>
              <Input
                type="text"
                placeholder="Jogador 1"
                ref={jogador1Ref}
              />
              <Input
                type="text"
                placeholder="Jogador 2"
                ref={jogador2Ref}
              />
              <Button
                onClick={() => {
                  const jogador1 = jogador1Ref.current?.value;
                  const jogador2 = jogador2Ref.current?.value;
                  if (jogador1 && jogador2) {
                    addDupla([jogador1, jogador2]);
                    if (jogador1Ref.current) jogador1Ref.current.value = '';
                    if (jogador2Ref.current) jogador2Ref.current.value = '';
                  } else {
                    alert('Por favor, insira os nomes dos dois jogadores.');
                  }
                }}
              >
                Adicionar Dupla
              </Button>
              <Button onClick={add8Duplas}>
                Adicionar 8 Duplas
              </Button>
            </div>
            <Button onClick={startTournament} disabled={duplas.length < 3}>
              Começar Torneio
            </Button>
          </>
        )}
        <section id="organogramaSection" className="section" style={{ display: faseAtual !== 'duplas' ? 'block' : 'none' }}>
          <h2>Organograma do Torneio</h2>
          <h3 id="faseAtual">
            {faseAtual === 'grupos' ? 'Fase de Grupos' :
              faseAtual === 'eliminatória' ? 'Fase Eliminatória' :
                'Torneio Finalizado'}
          </h3>
          {faseAtual === 'grupos' && (
            <FaseDeGrupos grupos={grupos} onAtualizarEstatisticas={atualizarEstatisticas} onUpdateGroups={setGrupos} />
          )}
          {faseAtual === 'eliminatória' && (
            <>
              <FaseEliminatoria
                fase="Fase Eliminatória"
                jogos={jogosEliminatoria}
                onAtualizarEstatisticas={atualizarEstatisticas}
              />
              {jogosEliminatoria.every(jogo => jogo.submetido) && (
                <React.Fragment>
                  {/* Chamada explícita de encerrarFaseEliminatoria */}
                  {(() => {
                    encerrarFaseEliminatoria();
                    const finalGame = jogosEliminatoria.find(jogo => jogo.fase === 'Final');
                    return finalGame ? (
                      <p>Final: {finalGame.dupla1.join(' e ')} vs {finalGame.dupla2.join(' e ')}</p>
                    ) : (
                      <p>Final: Não definida</p>
                    );
                  })()}
                  <h3>Disputa pelo Terceiro Lugar:</h3>
                  {(() => {
                    const thirdPlaceGame = jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar');
                    return thirdPlaceGame ? (
                      <p>Disputa pelo terceiro: {thirdPlaceGame.dupla1.join(' e ')} vs {thirdPlaceGame.dupla2.join(' e ')}</p>
                    ) : (
                      <p>Disputa pelo terceiro: Não definida</p>
                    );
                  })()}
                  <Button onClick={finalizarTorneio}>
                    Finalizar Torneio
                  </Button>
                </React.Fragment>
              )}
            </>
          )}
      {historicoJogos.length > 0 && (
        <section className="section">
          <h3>Histórico de Jogos:</h3>
          {grupos.map(grupo => (
            <div key={grupo.id}>
              <h4>Grupo {grupo.id + 1}</h4>
              <ul>
                {historicoJogos
                  .filter(jogo => jogo.grupo === grupo.id)
                  .map((jogo, index) => {
                    console.log(`Jogo index: ${jogo.jogoIndex}, Real index: ${index}`);
                    return (
                      <li key={jogo.jogoIndex}>
                        Rodada {jogo.fase || 'Desconhecida'}:
                        {Array.isArray(jogo.dupla1) ? jogo.dupla1.join(' e ') : 'Dupla Desconhecida'}
                        vs
                        {Array.isArray(jogo.dupla2) ? jogo.dupla2.join(' e ') : 'Dupla Desconhecida'}
                        - Placar: {jogo.placar || 'Não Submetido'}
                      </li>
                    );
                  })}
              </ul>
            </div>
          ))}
        </section>
          )}
          {historicoFases.length > 0 && (
            <section className="section">
              <h3>Histórico de Fases:</h3>
              {historicoFases.map((fase, index) => (
                <div key={index}>
                  <h4>{fase.fase}</h4>
                  <p>Classificados:</p>
                  <ul>
                    {fase.classificados.map((dupla, i) => (
                      <li key={i}>{dupla.join(' e ')}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </section>
          )}
          {faseAtual === 'grupos' && (
            <Button onClick={encerrarFaseGrupos}>
              Encerrar Fase de Grupos
            </Button>
          )}
          {faseAtual === 'finalizado' && (
            <>
              <p>O torneio foi finalizado. Parabéns aos vencedores!</p>
              <h3>Ranking Final:</h3>
              <ul>
                {ranking.map((posição, index) => (
                  <li key={index}>
                    {posição.lugar}º Lugar:
                    {Array.isArray(posição.dupla) ? posição.dupla.join(' e ') : posição.dupla || 'Desconhecido'}
                  </li>
                ))}
              </ul>
            </>
          )}
        </section>
      </main>
      <footer>
        <p className="developed-by">Desenvolvido por Antônio Fichina Netto - 2024</p>
      </footer>
    </div>
  );
}

export default App;
