// Função para dividir as duplas em grupos conforme o regulamento do torneio
export const dividirGrupos = (duplas) => {
  if (duplas.length < 3) {
    throw new Error("É necessário pelo menos 3 duplas para formar grupos.");
  }
  
  let numGrupos;
  if (duplas.length <= 5) numGrupos = 1;
  else if (duplas.length <= 8) numGrupos = 2;
  else if (duplas.length <= 11) numGrupos = 3;
  else if (duplas.length <= 14) numGrupos = 4;
  else if (duplas.length <= 17) numGrupos = 5;
  else numGrupos = 6;

  let grupos = [];
  let duplasPorGrupo = Math.floor(duplas.length / numGrupos);
  let resto = duplas.length % numGrupos;

  for (let i = 0; i < numGrupos; i++) {
    let grupoSize = duplasPorGrupo + (i < resto ? 1 : 0);
    grupos.push({
      id: i,
      duplas: duplas.splice(0, grupoSize),
      jogos: []
    });
  }

  // Ajuste final para garantir que cada grupo tenha pelo menos 3 duplas se possível
  if (numGrupos > 1) {
    while (grupos.some(g => g.duplas.length < 3) && duplas.length > 0) {
      let menorGrupo = grupos.reduce((menor, grupo) => grupo.duplas.length < menor.duplas.length ? grupo : menor);
      menorGrupo.duplas.push(duplas.shift());
    }
  }

  // Atualizar jogos para cada grupo
  grupos.forEach(grupo => {
    grupo.jogos = criarJogosParaGrupo(grupo.duplas, grupo.id);
  });

  return grupos;
};
  
  // Função para criar jogos para um grupo específico
  export const criarJogosParaGrupo = (grupo, grupoIndex) => {
    let jogosGrupo = [];
    for (let i = 0; i < grupo.length; i++) {
      for (let j = i + 1; j < grupo.length; j++) {
        jogosGrupo.push({
          dupla1: grupo[i],
          dupla2: grupo[j],
          placar: '',
          submetido: false,
          grupoIndex,
          jogoIndex: jogosGrupo.length
        });
      }
    }
    return jogosGrupo;
  };
  
  // Objeto para armazenar as estatísticas das duplas
  export const duplasEstatisticas = {};
  
  
  // Função para classificar duplas por desempate
  export const classificarDuplasPorDesempate = (grupo, jogos) => {
    return grupo.sort((a, b) => {
      let statsA = duplasEstatisticas[a.join(',')] || { pontos: 0, setsVencidos: 0, setsPerdidos: 0 };
      let statsB = duplasEstatisticas[b.join(',')] || { pontos: 0, setsVencidos: 0, setsPerdidos: 0 };
      
      if (statsB.pontos !== statsA.pontos) return statsB.pontos - statsA.pontos;
      let saldoA = statsA.setsVencidos - statsA.setsPerdidos;
      let saldoB = statsB.setsVencidos - statsB.setsPerdidos;
      if (saldoA !== saldoB) return saldoB - saldoA;
  
      // Confronto direto
      const jogoDireto = jogos.find(j => 
        (j.dupla1.join(',') === a.join(',') && j.dupla2.join(',') === b.join(',')) ||
        (j.dupla1.join(',') === b.join(',') && j.dupla2.join(',') === a.join(','))
      );
      if (jogoDireto) {
        const [set1, set2] = jogoDireto.placar.split('-').map(Number);
        return jogoDireto.dupla1.join(',') === a.join(',') ? set1 - set2 : set2 - set1;
      }
  
      // Se ainda estiver empatado, você pode adicionar mais critérios ou usar um sorteio
      return 0;
    });
  };
