// src/utils/torneioUtils.js
export const classificarDuplas = (grupos) => {
  // Lógica de classificação baseada nos resultados dos grupos, aqui é uma simplificação
  return grupos.flatMap(grupo => {
    return grupo.jogos.reduce((classificados, jogo) => {
      const vencedor = jogo.placar.split('-').map(Number)[0] > jogo.placar.split('-').map(Number)[1] ? jogo.dupla1 : jogo.dupla2;
      if (!classificados.some(d => d.every((player, index) => player === vencedor[index]))) {
        classificados.push(vencedor);
      }
      return classificados;
    }, []);
  }).slice(0, 4); // Top 4 para semifinais
};

export const criarJogosParaGrupo = (duplas, grupoId) => {
  const jogos = [];
  for (let i = 0; i < duplas.length; i++) {
    for (let j = i + 1; j < duplas.length; j++) {
      jogos.push({
        dupla1: duplas[i],
        dupla2: duplas[j],
        placar: '',
        submetido: false,
        grupoId
      });
    }
  }
  return jogos;
};

export const dividirGrupos = (duplas) => {
  let numGrupos = Math.ceil(duplas.length / 3); // 3 é o número mínimo de duplas por grupo
  
  if (duplas.length <= 5) numGrupos = 1; // Grupo único para até 5 duplas
  else if (duplas.length <= 8) numGrupos = 2;
  else if (duplas.length <= 11) numGrupos = 3;
  else if (duplas.length <= 14) numGrupos = 4;
  else if (duplas.length <= 17) numGrupos = 5;
  else if (duplas.length <= 20) numGrupos = 6;

  const grupos = Array(numGrupos).fill().map((_, i) => ({
    id: i,
    duplas: duplas.slice(i * Math.ceil(duplas.length / numGrupos), (i + 1) * Math.ceil(duplas.length / numGrupos))
  }));

  return grupos;
};

export const classificarDuplasPorDesempate = (duplas, jogos) => {
  const resultados = duplas.map(d => ({
    dupla: d,
    vitorias: 0,
    saldoGames: 0,
    jogos: []
  }));

  jogos.forEach(jogo => {
    const resultado1 = resultados.find(r => r.dupla === jogo.dupla1);
    const resultado2 = resultados.find(r => r.dupla === jogo.dupla2);
    
    if (jogo.placar) {
      const [games1, games2] = jogo.placar.split('-').map(Number);
      if (games1 > games2) {
        resultado1.vitorias++;
      } else if (games2 > games1) {
        resultado2.vitorias++;
      }
      resultado1.saldoGames += games1 - games2;
      resultado2.saldoGames += games2 - games1;
    }

    resultado1.jogos.push(jogo);
    resultado2.jogos.push(jogo);
  });

  // Ordenar por número de vitórias
  resultados.sort((a, b) => b.vitorias - a.vitorias);

  // Se houver empate em vitórias, ordenar por saldo de games
  let i = 0;
  while (i < resultados.length - 1) {
    if (resultados[i].vitorias === resultados[i + 1].vitorias) {
      let j = i + 1;
      while (j < resultados.length && resultados[i].vitorias === resultados[j].vitorias) {
        j++;
      }
      const empate = resultados.slice(i, j).sort((a, b) => b.saldoGames - a.saldoGames);
      resultados.splice(i, empate.length, ...empate);
      i = j;
    } else {
      i++;
    }
  }

  // Note: Para 3 duplas empatadas, você precisaria de lógica adicional para confronto direto ou sorteio.
  // Para 2 duplas, o confronto direto já resolve.

  return resultados.map(r => r.dupla);
};
