    if (jogo.dupla2[0] === 'BYE' || isNaN(score1) || isNaN(score2)) {
      return jogo.dupla1;
    }
    return score1 > score2 ? jogo.dupla1 : jogo.dupla2;
  });

  let rodadasTotal = Math.ceil(Math.log2(vencedores.length));
  let novaFaseNome;
  if (rodadasTotal === 1) {
    novaFaseNome = 'Final';
  } else if (rodadasTotal === 2) {
    novaFaseNome = 'Semifinal';
  } else if (rodadasTotal === 3) {
    novaFaseNome = 'Quartas de Final';
  } else {
    novaFaseNome = `Rodada ${jogosRodadaAtual[0].rodada + 1}`;
  }

  // Ajusta para BYEs se necessário
  let proximoMultiplo = Math.pow(2, Math.ceil(Math.log2(vencedores.length)));
  let byes = proximoMultiplo - vencedores.length;
  let vencedoresAjustados = [...vencedores];
  for (let i = 0; i < byes; i++) {
    vencedoresAjustados.push(['BYE', '']);
  }

  // Gera os jogos da nova rodada
  let novaRodada = [];
  for (let i = 0; i < vencedoresAjustados.length; i += 2) {
    novaRodada.push({
      dupla1: vencedoresAjustados[i],
      dupla2: vencedoresAjustados[i + 1],
      placar: '',
      submetido: false,
      fase: novaFaseNome,
