import React, { useState, useEffect } from 'react';
import Jogo from './Jogo';

const FaseDeGrupos = ({ grupos, onAtualizarEstatisticas, onUpdateGroups, historicoJogos }) => {
  const [gruposState, setGruposState] = useState(grupos);

  useEffect(() => {
    setGruposState(grupos);
  }, [grupos]);

  useEffect(() => {
    if (onUpdateGroups) {
      onUpdateGroups(gruposState);
    }
  }, [gruposState, onUpdateGroups]);

  // Adicionar este useEffect para sincronização
  useEffect(() => {
    if (Array.isArray(historicoJogos)) {
      gruposState.forEach(grupo => {
        grupo.jogos.forEach(jogo => {
          if (jogo.submetido && !historicoJogos.some(hj => hj.jogoIndex === jogo.jogoIndex)) {
            onAtualizarEstatisticas({
              ...jogo,
              grupo: grupo.id,
              fase: 'Fase de Grupos',
              jogoIndex: grupo.id * 100 + jogo.jogoIndex
            });
          }
        });
      });
    } else {
      console.warn('historicoJogos is not an array or is undefined');
    }
  }, [gruposState, historicoJogos, onAtualizarEstatisticas]);

  const handleSubmitScore = (placar, jogoIndex, grupoIndex) => {
    setGruposState(prevGrupos => {
      const updatedGroups = prevGrupos.map(grupo =>
        grupo.id === grupoIndex ? {
          ...grupo,
          jogos: grupo.jogos.map(jogo =>
            jogo.jogoIndex === jogoIndex && !jogo.submetido ? 
              { ...jogo, placar: placar, submetido: true } : jogo
          )
        } : grupo
      );
      console.log('Grupos após atualização:', updatedGroups);
      return updatedGroups;
    });
  };

  // Função para preencher e submeter todos os placares automaticamente com 6-2
  const preencherTodosOsPlacares = () => {
    setGruposState(prevGrupos => {
      const newGroups = prevGrupos.map(grupo => ({
        ...grupo,
        jogos: grupo.jogos.map(jogo => {
          if (!jogo.submetido) {
            onAtualizarEstatisticas({ ...jogo, placar: '6-2' });
            return { ...jogo, placar: '6-2', submetido: true };
          }
          return jogo;
        })
      }));
      console.log('Novos grupos após preenchimento:', newGroups);
      return newGroups;
    });
  };

  return (
    <div>
      <h2>Fase de Grupos</h2>
      {gruposState.map(grupo => (
        <div key={grupo.id}>
          <h3>Grupo {grupo.id + 1}</h3>
          {grupo.jogos.map(jogo => (
            <Jogo
              key={grupo.id * 100 + jogo.jogoIndex} 
              jogo={jogo}
              onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex, grupo.id)}
            />
          ))}
        </div>
      ))}
      {/* Botão temporário para preencher todos os placares com 6-2 */}
      <button onClick={preencherTodosOsPlacares}>Preencher e Submeter Todos os Placar</button>
    </div>
  );
};

export default FaseDeGrupos;
