// MeusTorneios.js (corrigido)
import React, { useState } from 'react';

function MeusTorneios({ tournaments, onSelectTournament, onCreateNew, onDeleteTournament }) {
  const [novoTorneioNome, setNovoTorneioNome] = useState(''); // Estado temporário

  const handleCreate = () => {
    if (novoTorneioNome.trim()) {
      onCreateNew(novoTorneioNome); // Passa o nome apenas ao confirmar
      setNovoTorneioNome(''); // Limpa o campo após criar
    } else {
      alert('Digite um nome para o torneio.');
    }
  };

  return (
    <div>
      <h2>Meus Torneios</h2>
      <input
        type="text"
        value={novoTorneioNome}
        onChange={(e) => setNovoTorneioNome(e.target.value.toUpperCase())} // Apenas atualiza o estado
        placeholder="Digite o nome do novo torneio"
      />
      <button onClick={handleCreate}>Criar Novo Torneio</button>
      <ul>
        {Object.entries(tournaments).map(([nome, torneio]) => (
          <li key={nome}>
            {nome} ({torneio.status}) - Criado em{' '}
            {torneio.dataCriacao
              ? new Date(torneio.dataCriacao).toLocaleDateString('pt-BR')
              : 'Data inválida'}
            <button onClick={() => onSelectTournament(nome)}>Abrir</button>
            <button onClick={() => onDeleteTournament(nome)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MeusTorneios;
