import React, { useState } from 'react';
import Input from '../UI/Input';
import Button from '../UI/Button';
import './Jogo.css'; // Suponha que você tenha um arquivo CSS para este componente

const Jogo = ({ jogo, onSubmitScore }) => {
  const [placar, setPlacar] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmitScore = () => {
    if (placar.match(/^\d+-\d+$/)) {
      onSubmitScore(placar, jogo.jogoIndex);
      setIsSubmitted(true);
    } else {
      alert('Formato de placar inválido. Use "X-Y", por exemplo, "6-1".');
    }
  };

  const handleEditScore = () => {
    setIsSubmitted(false);
  };

  return (
    <div className="jogo">
      <p>Jogo {jogo.jogoIndex + 1}: {jogo.dupla1.join(' e ')} vs {jogo.dupla2.join(' e ')}</p>
      <div className="placar-submeter">
        <Input 
          value={placar} 
          onChange={(e) => !isSubmitted && setPlacar(e.target.value)} 
          placeholder="Placar (ex: 6-1)" 
          disabled={isSubmitted}
          readOnly={isSubmitted}
          className={isSubmitted ? 'read-only-input' : ''}
        />
        <Button onClick={isSubmitted ? handleEditScore : handleSubmitScore}>
          {isSubmitted ? 'Editar' : 'Submeter'}
        </Button>
      </div>
    </div>
  );
};

export default Jogo;
