              <th>#</th>
              <th>DATA/HORA</th>
              <th>JOGADORES 01</th>
              <th>PLACAR</th>
              <th>JOGADORES 02</th>
              <th>VENCEDOR</th>
            </tr>
          </thead>
          <tbody>
            {grupo.jogos.map((jogo, idx) => {
              const [g1, g2] = jogo.placar ? jogo.placar.split('-').map(Number) : [0, 0];
              const vencedor = jogo.placar
                ? g1 > g2 ? jogo.dupla1.join(' & ') : g2 > g1 ? jogo.dupla2.join(' & ') : 'Empate'
                : 'A definir';
              const incrementoMinutos = idx * 30;
