import React from 'react';
import Jogo from './Jogo';

const FaseDeGrupos = ({ grupos, onAtualizarEstatisticas }) => {
  const handleSubmitScore = (placar, jogoIndex, grupoIndex) => {
    // Lógica para submeter o placar de um jogo. 
    // Esta função deve atualizar o estado no componente pai.
    onAtualizarEstatisticas({ grupoIndex, jogoIndex, placar });
  };

  return (
    <div>
      <h2>Fase de Grupos</h2>
      {grupos.map(grupo => (
        <div key={grupo.id}>
          <h3>Grupo {grupo.id + 1}</h3>
          {grupo.jogos.map(jogo => (
            <Jogo key={jogo.jogoIndex} jogo={jogo} onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex, grupo.id)} />
          ))}
        </div>
      ))}
    </div>
  );
};

export default FaseDeGrupos;
