import React, { useState, useEffect } from 'react';
import DuplasList from './components/Duplas/DuplasList';
import FaseDeGrupos from './components/Torneio/FaseDeGrupos';
import FaseEliminatoria from './components/Torneio/FaseEliminatoria';
import Button from './components/UI/Button';
import { duplasEstatisticas } from './utils/torneioUtils';
import './App.css';
import {
  dividirGrupos,
  classificarDuplasPorDesempate
} from './utils/torneioUtils';

function App() {
  const [duplas, setDuplas] = useState([]);
  const [faseAtual, setFaseAtual] = useState('duplas');
  const [grupos, setGrupos] = useState([]);
  const [jogosEliminatoria, setJogosEliminatoria] = useState([]);
  const [resultados, setResultados] = useState({});

  useEffect(() => {
    console.log('Estado dos jogos eliminatórios atualizado:', jogosEliminatoria);
  }, [jogosEliminatoria]);


  // Função para adicionar duplas
  const addDupla = (dupla) => {
    if (!duplas.some(d => d.includes(dupla[0]) || d.includes(dupla[1]))) {
      setDuplas(prevDuplas => [...prevDuplas, dupla]);
    } else {
      alert('Os nomes dos jogadores devem ser únicos. Um ou ambos já estão registrados.');
    }
  };

  // Função para adicionar 8 duplas automaticamente
  const add8Duplas = () => {
    let newDuplas = [];
    for (let i = 1; i <= 8; i++) {
      newDuplas.push([`JOGADOR${i * 2 - 1}`, `JOGADOR${i * 2}`]);
    }
    setDuplas(prevDuplas => [...prevDuplas, ...newDuplas]);
  };

  // Função para remover duplas
  const removeDupla = (index) => {
    setDuplas(duplas.filter((_, i) => i !== index));
  };

  const startTournament = () => {
    if (duplas.length < 3) {
      alert('É necessário pelo menos 3 duplas para começar o torneio.');
      return;
    }
    const gruposCriados = dividirGrupos([...duplas].sort(() => Math.random() - 0.5));
    setGrupos(gruposCriados);
    setFaseAtual('grupos');
  };

  // Função para iniciar a fase eliminatória
  const iniciarFaseEliminatoria = (classificados) => {
    let jogosEliminatoria = [];

    // Calcular o número de rodadas necessárias para eliminar até um campeão
    let rodadas = Math.ceil(Math.log2(classificados.length));
    let duplasRestantes = classificados.length;

    for (let r = 1; r <= rodadas; r++) {
      let jogosDaRodada = [];
      for (let i = 0; i < duplasRestantes; i += 2) {
        jogosDaRodada.push({
          dupla1: classificados[i] || ['TBD', ''],
          dupla2: classificados[i + 1] || ['TBD', ''],
          placar: '',
          submetido: false,
          jogoIndex: jogosEliminatoria.length,
          fase: r === rodadas ? 'Final' : (r === rodadas - 1 ? 'Semifinal' : `Rodada ${r}`)
        });
      }
      jogosEliminatoria.push(...jogosDaRodada);
      duplasRestantes = Math.ceil(duplasRestantes / 2); // Arredondar para cima para lidar com números ímpares
    }

    setJogosEliminatoria(jogosEliminatoria);
    setFaseAtual('eliminatória');
  };

  // Função para encerrar a fase de grupos
  const encerrarFaseGrupos = () => {
    console.log('Estado dos grupos antes de verificar:', grupos);
    if (grupos.some(grupo => grupo.jogos.some(jogo => !jogo.submetido))) {
      alert('Todos os jogos devem ser submetidos antes de encerrar a fase de grupos.');
      return;
    }

    let classificados = [];
    grupos.forEach(grupo => {
      let classificadosGrupo = classificarDuplasPorDesempate(grupo.duplas, grupo.jogos);
      console.log('Classificados do grupo ', grupo.id, ':', classificadosGrupo); // Para debug
      classificados.push(...classificadosGrupo.slice(0, 2)); // Assume-se 2 primeiros por grupo
    });

    if (duplas.length <= 5) {
      // Apenas um campeão
      alert(`Campeão: ${classificados[0].join(' e ')}`);
      setFaseAtual('finalizado');
    } else {
      iniciarFaseEliminatoria(classificados); // Garantir que esta linha está sendo chamada
    }
    console.log('Classificados para a fase eliminatória:', classificados); // Para debug
  };

  const atualizarEstatisticas = (jogo) => {
    const { dupla1, dupla2, placar } = jogo;
    const [set1, set2] = placar.split('-').map(Number);
    const vencedor = set1 > set2 ? dupla1 : dupla2;

    setResultados(prevResultados => {
      const updateStats = (dupla, isWin) => {
        const duplaKey = dupla.join(',');
        return {
          ...(prevResultados[duplaKey] || { vitórias: 0, derrotas: 0, setsVencidos: 0, setsPerdidos: 0 }),
          vitórias: isWin ? (prevResultados[duplaKey]?.vitórias || 0) + 1 : (prevResultados[duplaKey]?.vitórias || 0),
          derrotas: isWin ? prevResultados[duplaKey]?.derrotas || 0 : (prevResultados[duplaKey]?.derrotas || 0) + 1,
          setsVencidos: (prevResultados[duplaKey]?.setsVencidos || 0) + (isWin ? set1 : set2),
          setsPerdidos: (prevResultados[duplaKey]?.setsPerdidos || 0) + (isWin ? set2 : set1)
        };
      };

      // Atualização para duplasEstatisticas
      const isDupla1Win = vencedor === dupla1;
      duplasEstatisticas[dupla1.join(',')] = {
        ...(duplasEstatisticas[dupla1.join(',')] || { pontos: 0, setsVencidos: 0, setsPerdidos: 0 }),
        pontos: isDupla1Win ? (duplasEstatisticas[dupla1.join(',')]?.pontos || 0) + 1 : (duplasEstatisticas[dupla1.join(',')]?.pontos || 0),
        setsVencidos: (duplasEstatisticas[dupla1.join(',')]?.setsVencidos || 0) + (isDupla1Win ? set1 : set2),
        setsPerdidos: (duplasEstatisticas[dupla1.join(',')]?.setsPerdidos || 0) + (isDupla1Win ? set2 : set1)
      };
      duplasEstatisticas[dupla2.join(',')] = {
        ...(duplasEstatisticas[dupla2.join(',')] || { pontos: 0, setsVencidos: 0, setsPerdidos: 0 }),
        pontos: !isDupla1Win ? (duplasEstatisticas[dupla2.join(',')]?.pontos || 0) + 1 : (duplasEstatisticas[dupla2.join(',')]?.pontos || 0),
        setsVencidos: (duplasEstatisticas[dupla2.join(',')]?.setsVencidos || 0) + (!isDupla1Win ? set1 : set2),
        setsPerdidos: (duplasEstatisticas[dupla2.join(',')]?.setsPerdidos || 0) + (!isDupla1Win ? set2 : set1)
      };

      return {
        ...prevResultados,
        [dupla1.join(',')]: updateStats(dupla1, isDupla1Win),
        [dupla2.join(',')]: updateStats(dupla2, !isDupla1Win)
      };
    });
  };

  // Função para determinar Vencedores
  const determinarVencedores = () => {
    if (jogosEliminatoria.length !== 1) {
      console.error("Erro: Esperava-se um jogo final.");
      return;
    }

    const final = jogosEliminatoria[0];
    const [set1, set2] = final.placar.split('-').map(Number);
    const campeao = set1 > set2 ? final.dupla1 : final.dupla2;
    const viceCampeao = campeao === final.dupla1 ? final.dupla2 : final.dupla1;

    // Usar os resultados das semifinais armazenados para determinar terceiro e quarto lugar
    const semifinalResults = resultados.semifinalResults;
    const terceiroLugar = semifinalResults[0].placar.split('-')[0] < semifinalResults[0].placar.split('-')[1] ? semifinalResults[0].dupla1 : semifinalResults[0].dupla2;
    const quartoLugar = semifinalResults[1].placar.split('-')[0] < semifinalResults[1].placar.split('-')[1] ? semifinalResults[1].dupla1 : semifinalResults[1].dupla2;

    return {
      primeiro: campeao,
      segundo: viceCampeao,
      terceiro: terceiroLugar,
      quarto: quartoLugar
    };
  };

  // Função para Encerrar Fase Eliminatória
  const encerrarFaseEliminatoria = () => {
    if (jogosEliminatoria.some(jogo => !jogo.submetido)) {
      alert('Todos os jogos devem ser submetidos antes de avançar.');
      return;
    }

    // Verificar se é a última rodada (final)
    const ultimaRodada = Math.max(...jogosEliminatoria.map(jogo => jogo.rodada));
    const jogosUltimaRodada = jogosEliminatoria.filter(jogo => jogo.rodada === ultimaRodada);

    if (jogosUltimaRodada.length === 1) { // Se só houver um jogo na última rodada, é a final
      setFaseAtual('final');
    } else {
      // Lógica para avançar para a próxima rodada
      setFaseAtual('eliminatória'); // Mantém a fase, mas ajusta a renderização para mostrar a próxima rodada
    }
  };

  // Novamente, após a final, definir o ranking
  const finalizarTorneio = () => {
    if (jogosEliminatoria.some(jogo => !jogo.submetido)) {
      alert('O jogo da final deve ser submetido antes de finalizar o torneio.');
      return;
    }
    const ranking = determinarVencedores();
    setResultados(prev => ({ ...prev, ranking }));
    setFaseAtual('finalizado');
  };

  return (
    <div className="App">
      <header>
        <h1>BT Connect</h1>
      </header>

      <main className="container">
        <section className="section">
          <h2>Informações do Torneio</h2>
          <label htmlFor="nomeTorneio">Nome do Torneio:</label>
          <input type="text" id="nomeTorneio" placeholder="Digite o nome do torneio" style={{ textTransform: 'uppercase' }} required />
          <label htmlFor="dataTorneio">Dia do Torneio:</label>
          <input type="date" id="dataTorneio" required />
        </section>

        <section className="section">
          <h2>Adicionar Duplas</h2>
          {faseAtual === 'duplas' &&
            <>
              <DuplasList duplas={duplas} onAddDupla={addDupla} onRemoveDupla={removeDupla} />
              <Button
                onClick={startTournament}
                disabled={duplas.length < 3}>
                Começar Torneio
              </Button>
              <Button onClick={add8Duplas}>Adicionar 8 Duplas (Teste)</Button>
            </>
          }
        </section>

        <section id="organogramaSection" className="section" style={{ display: faseAtual !== 'duplas' ? 'block' : 'none' }}>
          <h2>Organograma do Torneio</h2>
          <h3 id="faseAtual">{faseAtual === 'grupos' ? 'Fase de Grupos' : faseAtual === 'eliminatória' ? 'Fase Eliminatória' :
            faseAtual === 'terceiroLugar' ? 'Disputa pelo 3º Lugar' :
              faseAtual === 'final' ? 'Final' : 'Torneio Finalizado'}</h3>
          {faseAtual === 'grupos' &&
            <FaseDeGrupos grupos={grupos} onAtualizarEstatisticas={atualizarEstatisticas} onEncerrarFase={encerrarFaseGrupos} onUpdateGroups={setGrupos} />
          }
          {(faseAtual === 'eliminatória' || faseAtual === 'terceiroLugar' || faseAtual === 'final') &&
            <FaseEliminatoria
              fase={faseAtual === 'final' ? 'Final' : faseAtual === 'terceiroLugar' ? 'Disputa pelo 3º Lugar' : 'Fase Eliminatória'}
              jogos={jogosEliminatoria.filter(jogo => jogo.fase === faseAtual || !jogo.fase)} // Filtra jogos pela fase atual ou sem fase definida
              onAtualizarEstatisticas={atualizarEstatisticas}
              onUpdateJogos={(jogosAtualizados) => {
                setJogosEliminatoria(prev => prev.map(jogo =>
                  jogosAtualizados.find(updated => updated.jogoIndex === jogo.jogoIndex) || jogo
                ));
              }}
            />
          }
          {faseAtual === 'grupos' &&
            <Button onClick={encerrarFaseGrupos}>Encerrar Fase de Grupos</Button>
          }
          {faseAtual === 'eliminatória' &&
            <Button
              onClick={encerrarFaseEliminatoria}
              disabled={jogosEliminatoria.some(jogo => !jogo.submetido)}
            >
              Encerrar Semifinais
            </Button>
          }
          {faseAtual === 'terceiroLugar' &&
            <Button
              onClick={() => {
                if (jogosEliminatoria.some(jogo => jogo.fase === 'terceiroLugar' && !jogo.submetido)) {
                  alert('O jogo pelo 3º lugar deve ser submetido antes de avançar para a final.');
                  return;
                }
                setFaseAtual('final');
              }}
              disabled={jogosEliminatoria.some(jogo => jogo.fase === 'terceiroLugar' && !jogo.submetido)}
            >
              Avançar para a Final
            </Button>
          }
          {faseAtual === 'final' &&
            <Button
              onClick={finalizarTorneio}
              disabled={jogosEliminatoria.some(jogo => jogo.fase === 'final' && !jogo.submetido)}
            >
              Finalizar Torneio
            </Button>
          }
        </section>

        {/* Adicionando a seção de resultados finais */}
        <section className="section" style={{ display: faseAtual === 'finalizado' ? 'block' : 'none' }}>
          <h2>Resultado Final</h2>
          <ul>
            {resultados.ranking && Object.entries(resultados.ranking).map(([posicao, dupla]) => (
              <li key={posicao}>
                {posicao === 'primeiro' ? 'Primeiro Lugar' :
                  posicao === 'segundo' ? 'Segundo Lugar' :
                    posicao === 'terceiro' ? 'Terceiro Lugar' :
                      posicao === 'quarto' ? 'Quarto Lugar' : ''}: {dupla.join(' e ')}
              </li>
            ))}
          </ul>
        </section>
      </main>

      <footer>
        <p className="developed-by">Desenvolvido por Antônio Fichina Netto - 2024</p>
      </footer>
    </div>
  );
}

export default App;
