import React from 'react';
import Jogo from './Jogo';

const FaseEliminatoria = ({ fase, jogos, onAtualizarEstatisticas }) => {
  const handleSubmitScore = (placar, jogoIndex) => {
    onAtualizarEstatisticas({ jogoIndex, placar });
  };

  const jogosPorRodada = {};
  jogos.forEach(jogo => {
    if (!jogosPorRodada[jogo.rodada]) {
      jogosPorRodada[jogo.rodada] = [];
    }
    jogosPorRodada[jogo.rodada].push(jogo);
  });

  return (
    <div>
      <h2>{fase}</h2>
      {Object.keys(jogosPorRodada).sort((a, b) => a - b).map(rodada => (
        <div key={rodada}> // Usando rodada como chave aqui
          <h3>{rodada === '1' ? 'Primeira Rodada' : rodada === (Object.keys(jogosPorRodada).length).toString() ? 'Final' : rodada === (Object.keys(jogosPorRodada).length - 1).toString() ? 'Semifinal' : `Rodada ${rodada}`}</h3>
          {jogosPorRodada[rodada].map(jogo => (
            <Jogo 
              key={jogo.jogoIndex} // Garantindo que a chave seja única para cada jogo
              jogo={jogo} 
              onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex)}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default FaseEliminatoria;
