import React, { useState, useEffect } from 'react';
import Jogo from './Jogo';

const FaseEliminatoria = ({ fase, jogos, onAtualizarEstatisticas, onUpdateJogos }) => {
  const [jogosState, setJogosState] = useState(jogos);

  useEffect(() => {
    setJogosState(jogos); // Isso já sincroniza jogosState com jogos
  }, [jogos]);
  
  useEffect(() => {
    onUpdateJogos(jogosState);
  }, [jogosState, onUpdateJogos]); // Dependências claras

  const handleSubmitScore = (placar, jogoIndex) => {
    setJogosState(prevJogos => prevJogos.map(jogo => 
      jogo.jogoIndex === jogoIndex ? {...jogo, placar: placar, submetido: true} : jogo
    ));
    onAtualizarEstatisticas({...jogosState.find(j => j.jogoIndex === jogoIndex), placar});
  };

  return (
    <div>
      <h2>{fase}</h2>
      {jogosState.length > 0 ? (
        Array.from(new Set(jogosState.map(jogo => jogo.rodada))).sort().map(rodada => (
          <div key={rodada}>
            <h3>Rodada {rodada}</h3>
            {jogosState.filter(jogo => jogo.rodada === rodada).map(jogo => (
              <Jogo 
                key={jogo.jogoIndex}
                jogo={jogo} 
                onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex)}
              />
            ))}
          </div>
        ))
      ) : (
        <p>Nenhum jogo para mostrar.</p>
      )}
    </div>
  );
};

export default FaseEliminatoria;
