import React, { useState, useEffect } from 'react';
import DuplasList from './components/Duplas/DuplasList';
import FaseDeGrupos from './components/Torneio/FaseDeGrupos';
import FaseEliminatoria from './components/Torneio/FaseEliminatoria';
import { iniciarFaseEliminatoria, avancarRodadaEliminatoria } from './utils/torneioUtils';
import FaseFinal from './components/Torneio/FaseFinal';
import './App.css';
import { dividirGrupos, criarJogosParaGrupo, classificarDuplasPorDesempate } from './utils/torneioUtils';

function App() {
  const [duplas, setDuplas] = useState([]);
  const [faseAtual, setFaseAtual] = useState('duplas');
  const [grupos, setGrupos] = useState([]);
  const [jogosEliminatoria, setJogosEliminatoria] = useState([]);
  const [finalConfigurada, setFinalConfigurada] = useState(false);
  const [terceiroLugarConfigurado, setTerceiroLugarConfigurado] = useState(false);
  const [ranking, setRanking] = useState([]);

  useEffect(() => {
    // Pode ser usado para lógica de transição de fase ou atualização de UI
  }, [duplas, grupos, jogosEliminatoria]);


  const renderizarHistoricoEliminatoria = (jogos) => {
    // Obter as rodadas únicas, ordenadas
    const rodadas = [...new Set(jogos.map(j => j.rodada))].sort((a, b) => a - b);

    return rodadas.map(rodada => {
      const jogosDaRodada = jogos.filter(j => j.rodada === rodada);
      // Pega o nome da fase a partir do primeiro jogo da rodada
      const fase = jogosDaRodada[0]?.fase || '';
      return (
        <div key={rodada} className="rodada">
          <h3>{fase} - Rodada {rodada}</h3>
          {jogosDaRodada.map((jogo, index) => (
            <div key={index} className="jogo">
              <p>{jogo.dupla1.join(' & ')} vs {jogo.dupla2.join(' & ')}</p>
              <p>Placar: {jogo.placar} {jogo.submetido ? "(Submetido)" : "(Não submetido)"}</p>
            </div>
          ))}
        </div>
      );
    });
  };

  const addDupla = (dupla) => {
    if (!duplas.some(d => d.includes(dupla[0]) || d.includes(dupla[1]))) {
      setDuplas(prevDuplas => [...prevDuplas, dupla]);
    } else {
      alert('Os nomes dos jogadores devem ser únicos. Um ou ambos já estão registrados.');
    }
  };

  const removeDupla = (index) => {
    setDuplas(duplas.filter((_, i) => i !== index));
  };

  const startTournament = () => {
    if (duplas.length < 3) {
      alert('É necessário pelo menos 3 duplas para começar o torneio.');
      return;
    }
    // Sortear aleatoriamente as duplas antes de dividir em grupos
    const duplasSorteadas = [...duplas].sort(() => Math.random() - 0.5);
    const gruposCriados = dividirGrupos(duplasSorteadas);

    gruposCriados.forEach(grupo => {
      grupo.jogos = criarJogosParaGrupo(grupo.duplas, grupo.id);
    });
    setGrupos(gruposCriados);
    setFaseAtual('grupos');
  };

  const encerrarFaseGrupos = () => {
    if (grupos.some(grupo => grupo.jogos.some(jogo => !jogo.submetido))) {
      alert('Todos os jogos devem ser submetidos antes de encerrar a fase de grupos.');
      return;
    }

    // Se houver exatamente 2 grupos, use a qualificação por grupos
    if (grupos.length === 2) {
      const rankingGrupo1 = classificarDuplasPorDesempate(grupos[0].duplas, grupos[0].jogos);
      const rankingGrupo2 = classificarDuplasPorDesempate(grupos[1].duplas, grupos[1].jogos);
      // Qualificam-se os 2 melhores de cada grupo:
      const semifinalMatches = [
        {
          dupla1: rankingGrupo1[0], // 1º do Grupo 1
          dupla2: rankingGrupo2[1], // 2º do Grupo 2
          placar: '',
          submetido: false,
          fase: 'Semifinal',
          rodada: 1
        },
        {
          dupla1: rankingGrupo2[0], // 1º do Grupo 2
          dupla2: rankingGrupo1[1], // 2º do Grupo 1
          placar: '',
          submetido: false,
          fase: 'Semifinal',
          rodada: 1
        }
      ];
      setJogosEliminatoria(semifinalMatches);
      setFaseAtual('eliminatória');
    } else {
      // Caso contrário, usa a lógica antiga para outros formatos (por exemplo, mais de 2 grupos)
      const todosOsJogos = grupos.flatMap(grupo => grupo.jogos);
      let classificados = classificarDuplasPorDesempate(duplas, todosOsJogos);

      if (duplas.length <= 5) {
        alert(`Campeão: ${classificados[0].join(' e ')}`);
        setFaseAtual('finalizado');
      } else {
        const jogosRodada = iniciarFaseEliminatoria(classificados);
        setJogosEliminatoria(jogosRodada);
        setFaseAtual('eliminatória');
      }
    }
  };

  const atualizarPlacarNaEliminatoria = (jogo, placar) => {
    setJogosEliminatoria(prevJogos => {
      // Atualiza o placar do jogo
      const updatedJogos = prevJogos.map(j =>
        j === jogo ? { ...j, placar: placar, submetido: true } : j
      );

      // Determina a rodada atual com base no maior número de rodada dos jogos
      const rodadaAtual = Math.max(...updatedJogos.map(j => j.rodada));
      const jogosRodadaAtual = updatedJogos.filter(j => j.rodada === rodadaAtual);

      // Se todos os jogos da rodada atual foram submetidos...
      if (jogosRodadaAtual.every(j => j.submetido)) {
        // Se for a semifinal, avançamos para a final
        if (jogosRodadaAtual.some(j => j.fase === 'Semifinal') && !finalConfigurada) {
          iniciarFinal(updatedJogos);
        } else if (jogosRodadaAtual.length > 1) {
          // Se for outra rodada, cria a próxima fase eliminatória
          const novaRodada = avancarRodadaEliminatoria(jogosRodadaAtual);
          return [...updatedJogos, ...novaRodada];
        }
      }
      return updatedJogos;
    });
  };


  const iniciarFinal = (jogos) => {
    const finalistas = jogos.filter(jogo => jogo.fase === 'Semifinal').map(jogo => {
      const [set1, set2] = jogo.placar.split('-').map(Number);
      return set1 > set2 ? jogo.dupla1 : jogo.dupla2;
    });

    if (finalistas.length === 2) {
      setJogosEliminatoria([
        {
          dupla1: finalistas[0],
          dupla2: finalistas[1],
          placar: '',
          submetido: false,
          fase: 'Final'
        },
        {
          dupla1: jogos[0].dupla1 === finalistas[0] ? jogos[0].dupla2 : jogos[0].dupla1,
          dupla2: jogos[1].dupla1 === finalistas[1] ? jogos[1].dupla2 : jogos[1].dupla1,
          placar: '',
          submetido: false,
          fase: 'Disputa 3º Lugar'
        }
      ]);
      setFinalConfigurada(true);
      setTerceiroLugarConfigurado(true);
    } else {
      console.error('Não foi possível determinar os finalistas.');
    }
  };

  const atualizarPlacarNaFinal = (jogo, placar) => {
    setJogosEliminatoria(prevJogos => prevJogos.map(j =>
      j === jogo ? { ...j, placar: placar, submetido: true } : j
    ));
  };

  const encerrarFaseEliminatoria = () => {
    const rodadaAtual = Math.max(...jogosEliminatoria.map(j => j.rodada));
    if (jogosEliminatoria.filter(j => j.rodada === rodadaAtual).every(j => j.submetido)) {
      if (rodadaAtual === Math.max(...jogosEliminatoria.map(j => j.rodada))) {
        // Se todos os jogos da última rodada foram submetidos, avança para a final
        iniciarFinal(jogosEliminatoria);
        setFaseAtual('final');
      } else {
        // Aqui você pode adicionar lógica para avançar para a próxima rodada se não for a última
        alert('Avançando para a próxima rodada...');
      }
    } else {
      alert('Todos os jogos da rodada atual devem ser submetidos antes de avançar.');
    }
  };

  const finalizarTorneio = () => {
    const final = jogosEliminatoria.find(jogo => jogo.fase === 'Final');
    const terceiroLugar = jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar');

    const [vencedorFinal, perdedorFinal] = final.placar.split('-').map(Number)[0] > final.placar.split('-').map(Number)[1]
      ? [final.dupla1, final.dupla2]
      : [final.dupla2, final.dupla1];

    const [terceiro, quarto] = terceiroLugar.placar.split('-').map(Number)[0] > terceiroLugar.placar.split('-').map(Number)[1]
      ? [terceiroLugar.dupla1, terceiroLugar.dupla2]
      : [terceiroLugar.dupla2, terceiroLugar.dupla1];

    setRanking([
      { lugar: 1, dupla: vencedorFinal },
      { lugar: 2, dupla: perdedorFinal },
      { lugar: 3, dupla: terceiro },
      { lugar: 4, dupla: quarto }
    ]);

    setFaseAtual('finalizado');
  };

  // Função para adicionar automaticamente 8 duplas
  const add8Duplas = () => {
    const newDuplas = [];
    for (let i = 1; i <= 8; i++) {
      newDuplas.push([`Jogador${i * 2 - 1}`, `Jogador${i * 2}`]);
    }
    setDuplas(prevDuplas => [...prevDuplas, ...newDuplas]);
  };


  return (
    <div className="App">
      <header>
        <h1>Gerenciador de Torneio de Beach Tennis</h1>
      </header>
      <main>
        {faseAtual === 'duplas' && (
          <>
            <DuplasList duplas={duplas} onAddDupla={addDupla} onRemoveDupla={removeDupla} />
            <button className="iniciar-torneio" onClick={startTournament}>Iniciar Torneio</button>
            <button onClick={add8Duplas}>Adicionar 8 Duplas</button>
          </>
        )}

        {faseAtual === 'grupos' && (
          <>
            <FaseDeGrupos grupos={grupos} onUpdateGroups={setGrupos} />
            <button onClick={encerrarFaseGrupos}>Encerrar Fase de Grupos</button>
          </>
        )}

        {faseAtual === 'eliminatória' && (
          <>
            <h2>Fase Eliminatória</h2>
            <FaseEliminatoria
              jogos={jogosEliminatoria}
              onAtualizarPlacar={atualizarPlacarNaEliminatoria}
            />
            {renderizarHistoricoEliminatoria(jogosEliminatoria)}

            {/* Garantir que os jogos da final sejam criados antes de mudar de fase */}
            {finalConfigurada ? (
              <button onClick={() => {
                iniciarFinal(jogosEliminatoria);  // Criar os jogos da final e disputa de 3º lugar
                setFaseAtual('final'); // Só então avançar para a fase final
              }}>
                Avançar para a Final
              </button>
            ) : (
              <button onClick={encerrarFaseEliminatoria}>Encerrar Etapa</button>
            )}
          </>
        )}

        {faseAtual === 'final' && (
          <>
            <h2>Final do Torneio</h2>
            <FaseFinal
              jogos={jogosEliminatoria.filter(
                jogo => jogo.fase === 'Final' || jogo.fase === 'Disputa 3º Lugar'
              )}
              onAtualizarPlacar={atualizarPlacarNaFinal}
              onFinalizarTorneio={finalizarTorneio}
            />
            {terceiroLugarConfigurado && <p>A disputa pelo 3º lugar está configurada!</p>}

            {/* Mostrar botão "Encerrar Campeonato" somente quando todos os jogos finais forem submetidos */}
            {jogosEliminatoria
              .filter(jogo => jogo.fase === 'Final' || jogo.fase === 'Disputa 3º Lugar')
              .every(jogo => jogo.submetido) && (
                <button onClick={finalizarTorneio}>Encerrar Campeonato</button>
              )}
          </>
        )}

        {faseAtual === 'finalizado' && (
          <>
            <p>Torneio Finalizado!</p>
            <h3>Ranking Final:</h3>
            <ul>
              {ranking.map((posição, index) => (
                <li key={index}>
                  {posição.lugar}º Lugar: {posição.dupla.join(' & ')}
                </li>
              ))}
            </ul>
          </>
        )}
      </main>
    </div>
  );


}

export default App;
