import React, { useState, useEffect, useRef } from 'react';
import DuplasList from './components/Duplas/DuplasList';
import FaseDeGrupos from './components/Torneio/FaseDeGrupos';
import FaseEliminatoria from './components/Torneio/FaseEliminatoria';
import Button from './components/UI/Button';
import Input from './components/UI/Input';
import './App.css';
import {
  dividirGrupos,
  classificarDuplasPorDesempate,
  criarJogosParaGrupo
} from './utils/torneioUtils';

function App() {
  const [duplas, setDuplas] = useState([]);
  const [faseAtual, setFaseAtual] = useState('duplas');
  const [grupos, setGrupos] = useState([]);
  const [jogosEliminatoria, setJogosEliminatoria] = useState([]);
  const [ranking, setRanking] = useState([]);
  const [historicoJogos, setHistoricoJogos] = useState([]);
  const [historicoFases, setHistoricoFases] = useState([]);
  const [finalConfigurada, setFinalConfigurada] = useState(false);

  useEffect(() => {
    console.log('Estado dos jogos eliminatórios atualizado:', jogosEliminatoria);
  }, [jogosEliminatoria]);

  const jogador1Ref = useRef(null);
  const jogador2Ref = useRef(null);

  const addDupla = (dupla) => {
    if (!duplas.some(d => d.includes(dupla[0]) || d.includes(dupla[1]))) {
      setDuplas(prevDuplas => [...prevDuplas, dupla]);
    } else {
      alert('Os nomes dos jogadores devem ser únicos. Um ou ambos já estão registrados.');
    }
  };

  const removeDupla = (index) => {
    setDuplas(duplas.filter((_, i) => i !== index));
  };

  const startTournament = () => {
    if (duplas.length < 3) {
      alert('É necessário pelo menos 3 duplas para começar o torneio.');
      return;
    }
    const gruposCriados = dividirGrupos([...duplas].sort(() => Math.random() - 0.5));
    gruposCriados.forEach(grupo => {
      grupo.jogos = criarJogosParaGrupo(grupo.duplas, grupo.id);
    });
    setGrupos(gruposCriados);
    setFaseAtual('grupos');
  };

  const encerrarFaseGrupos = () => {
    if (grupos.some(grupo => grupo.jogos.some(jogo => !jogo.submetido))) {
      alert('Todos os jogos devem ser submetidos antes de encerrar a fase de grupos.');
      return;
    }

    let classificados = classificarDuplasPorDesempate([...duplas], grupos.flatMap(grupo => grupo.jogos), grupos);
    setHistoricoFases(prevHistorico => [...prevHistorico, {
      fase: 'Fase de Grupos',
      resultados: grupos.map(grupo => ({
        grupoId: grupo.id,
        jogos: grupo.jogos.map(jogo => ({
          dupla1: jogo.dupla1,
          dupla2: jogo.dupla2,
          placar: jogo.placar,
          vencedor: jogo.placar.split('-').map(Number)[0] > jogo.placar.split('-').map(Number)[1] ? jogo.dupla1 : jogo.dupla2,
          diferencaSets: Math.abs(jogo.placar.split('-').map(Number)[0] - jogo.placar.split('-').map(Number)[1])
        }))
      })),
      classificados: classificados
    }]);

    if (duplas.length <= 5) {
      alert(`Campeão: ${classificados[0].join(' e ')}`);
      setFaseAtual('finalizado');
    } else {
      iniciarFaseEliminatoria(classificados);
    }
  };

  const iniciarFaseEliminatoria = (classificados) => {
    console.log('Classificados para a fase eliminatória:', classificados);
    let jogos = [];
    let currentIndex = 0;

    if (classificados.length === 4) { // 4 duplas classificadas para as semifinais
      // Criar as semifinais
      jogos.push({
        dupla1: classificados[0], // Primeiro do Grupo 1
        dupla2: classificados[3], // Segundo do Grupo 2
        placar: '',
        submetido: false,
        jogoIndex: currentIndex++,
        rodada: 1,
        fase: 'Semifinal'
      });
      jogos.push({
        dupla1: classificados[2], // Segundo do Grupo 1
        dupla2: classificados[1], // Primeiro do Grupo 2
        placar: '',
        submetido: false,
        jogoIndex: currentIndex++,
        rodada: 1,
        fase: 'Semifinal'
      });

      // A final será adicionada posteriormente, quando os vencedores forem conhecidos.
    } else {
      // Lógica para torneios com mais de 4 classificados (usando BYEs se necessário)
      let rodadas = Math.ceil(Math.log2(classificados.length));
      let nextPowerOf2 = Math.pow(2, rodadas);
      let byes = nextPowerOf2 - classificados.length;

      for (let i = 0; i < byes; i++) {
        classificados.push(['BYE', '']);
      }

      for (let r = 1; r <= rodadas; r++) {
        let jogosDaRodada = [];
        for (let i = 0; i < classificados.length; i += 2) {
          let dupla1 = classificados[i];
          let dupla2 = classificados[i + 1] || ['BYE', ''];

          let jogoConfig = {
            placar: '',
            submetido: false,
            jogoIndex: currentIndex++,
            rodada: r,
            fase: r === rodadas ? 'Final' : (r === rodadas - 1 ? 'Semifinal' : `Rodada ${r}`)
          };

          if (dupla1[0] !== 'BYE' && dupla2[0] !== 'BYE') {
            jogosDaRodada.push({ ...jogoConfig, dupla1: dupla1, dupla2: dupla2 });
          } else if (dupla1[0] === 'BYE') {
            jogosDaRodada.push({ ...jogoConfig, dupla1: dupla2, dupla2: ['BYE', ''], placar: 'BYE', submetido: true });
          } else {
            jogosDaRodada.push({ ...jogoConfig, dupla1: dupla1, dupla2: ['BYE', ''], placar: 'BYE', submetido: true });
          }
        }
        jogos.push(...jogosDaRodada);

        let novosClassificados = [];
        for (let i = 0; i < classificados.length; i += 2) {
          if (classificados[i][0] === 'BYE') {
            if (i + 1 < classificados.length) {
              novosClassificados.push(classificados[i + 1]);
            }
          } else if (i + 1 < classificados.length && classificados[i + 1][0] === 'BYE') {
            novosClassificados.push(classificados[i]);
          } else {
            novosClassificados.push(classificados[i]); // Aqui pode ser ajustado para definir um vencedor corretamente
          }
        }
        classificados = novosClassificados;
      }
    }

    console.log('Jogos eliminatórios criados:', jogos);
    setJogosEliminatoria(jogos);
    setFaseAtual('eliminatória');
  };

  // Função que cria a final quando ambas as semifinais forem concluídas
  const verificarCriacaoFinal = (jogos) => {
    const semifinal1 = jogos.find(j => j.fase === 'Semifinal' && j.jogoIndex === 0);
    const semifinal2 = jogos.find(j => j.fase === 'Semifinal' && j.jogoIndex === 1);

    if (semifinal1 && semifinal2 && semifinal1.submetido && semifinal2.submetido) {
      const final = {
        dupla1: semifinal1.placar === 'V' ? semifinal1.dupla1 : semifinal1.dupla2,
        dupla2: semifinal2.placar === 'V' ? semifinal2.dupla1 : semifinal2.dupla2,
        placar: '',
        submetido: false,
        jogoIndex: jogos.length,
        rodada: 2,
        fase: 'Final'
      };

      setJogosEliminatoria([...jogos, final]);
    }
  };

  const encerrarFaseEliminatoria = () => {
    if (jogosEliminatoria.some(jogo => !jogo.submetido)) {
      alert('Todos os jogos devem ser submetidos antes de avançar para a próxima rodada.');
      return;
    }

    let ultimaRodada = Math.max(...jogosEliminatoria.map(jogo => jogo.rodada));
    let jogosUltimaRodada = jogosEliminatoria.filter(jogo => jogo.rodada === ultimaRodada);

    if (jogosUltimaRodada.length === 2) { // Semifinais concluídas
      let vencedores = jogosUltimaRodada.map(jogo => {
        const [set1, set2] = jogo.placar.split('-').map(Number);
        return set1 > set2 ? jogo.dupla1 : jogo.dupla2;
      });

      setJogosEliminatoria(prevJogos => {
        const newJogos = [...prevJogos];
        const indiceFinal = newJogos.findIndex(jogo => jogo.fase === 'Final');
        if (indiceFinal !== -1) {
          newJogos[indiceFinal] = {
            ...newJogos[indiceFinal],
            dupla1: vencedores[0],
            dupla2: vencedores[1]
          };
        }
        return newJogos;
      });

      // Adicionar disputa pelo terceiro lugar
      let perdedores = jogosUltimaRodada.map(jogo => {
        const [set1, set2] = jogo.placar.split('-').map(Number);
        return set1 > set2 ? jogo.dupla2 : jogo.dupla1;
      });

      setJogosEliminatoria(prevJogos => [
        ...prevJogos,
        {
          dupla1: perdedores[0],
          dupla2: perdedores[1],
          placar: '',
          submetido: false,
          jogoIndex: prevJogos.length,
          rodada: ultimaRodada + 1,
          fase: 'Disputa 3º Lugar'
        }
      ]);

      setFinalConfigurada(true); // Indica que a final foi configurada
    } else if (jogosUltimaRodada.length === 1) {
      const final = jogosUltimaRodada[0];
      const vencedorFinal = final.placar.split('-').map(Number)[0] > final.placar.split('-').map(Number)[1] ? final.dupla1 : final.dupla2;
      const perdedorFinal = vencedorFinal === final.dupla1 ? final.dupla2 : final.dupla1;
      finalizarTorneio(vencedorFinal, perdedorFinal);
    }
  };

  const atualizarEstatisticas = (jogoInfo) => {
    // Certifique-se de que historicoJogos é um array antes de tentar adicionar ou verificar elementos
    if (Array.isArray(historicoJogos)) {
      if (!historicoJogos.some(historicoJogo => historicoJogo.jogoIndex === jogoInfo.jogoIndex)) {
        setHistoricoJogos(prevHistorico => [...prevHistorico, { ...jogoInfo }]);
      } else {
        console.warn('Jogo já existente no histórico:', jogoInfo.jogoIndex);
      }
    } else {
      console.error('historicoJogos não é um array:', historicoJogos);
      // Considerar inicializar ou corrigir historicoJogos aqui
      setHistoricoJogos([{ ...jogoInfo }]);  // Reinicializar como array com o novo jogo
    }

    setJogosEliminatoria(prevJogos => {
      const updatedJogos = prevJogos.map(jogo =>
        jogo.jogoIndex === jogoInfo.jogoIndex ? { ...jogo, placar: jogoInfo.placar, submetido: true } : jogo
      );

      return updatedJogos;
    });
  };

  const finalizarTorneio = (vencedorFinal, perdedorFinal) => {
    let disputaTerceiroLugar = jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar');

    if (disputaTerceiroLugar && disputaTerceiroLugar.submetido) {
      let terceiroLugar = disputaTerceiroLugar.placar.split('-').map(Number)[0] > disputaTerceiroLugar.placar.split('-').map(Number)[1] ? disputaTerceiroLugar.dupla1 : disputaTerceiroLugar.dupla2;
      let quartoLugar = terceiroLugar === disputaTerceiroLugar.dupla1 ? disputaTerceiroLugar.dupla2 : disputaTerceiroLugar.dupla1;

      setRanking([
        { lugar: 1, dupla: vencedorFinal },
        { lugar: 2, dupla: perdedorFinal },
        { lugar: 3, dupla: terceiroLugar },
        { lugar: 4, dupla: quartoLugar }
      ]);

      setFaseAtual('finalizado');
    }
  };

  const add8Duplas = () => {
    let newDuplas = [];
    for (let i = 1; i <= 8; i++) {
      newDuplas.push([`JOGADOR${i * 2 - 1}`, `JOGADOR${i * 2}`]);
    }
    setDuplas(prevDuplas => [...prevDuplas, ...newDuplas]);
  };

  return (
    <div className="App">
      <header>
        <h1>BT Connect</h1>
      </header>
      <main className="container">
        <section className="section">
          <h2>Informações do Torneio</h2>
          <label htmlFor="nomeTorneio">Nome do Torneio:</label>
          <input type="text" id="nomeTorneio" placeholder="Digite o nome do torneio" required />
          <label htmlFor="dataTorneio">Dia do Torneio:</label>
          <input type="date" id="dataTorneio" required />
        </section>
        {faseAtual === 'duplas' && (
          <>
            <DuplasList duplas={duplas} onRemoveDupla={removeDupla} />
            <div>
              <Input
                type="text"
                placeholder="Jogador 1"
                ref={jogador1Ref}
              />
              <Input
                type="text"
                placeholder="Jogador 2"
                ref={jogador2Ref}
              />
              <Button
                onClick={() => {
                  const jogador1 = jogador1Ref.current?.value;
                  const jogador2 = jogador2Ref.current?.value;
                  if (jogador1 && jogador2) {
                    addDupla([jogador1, jogador2]);
                    if (jogador1Ref.current) jogador1Ref.current.value = '';
                    if (jogador2Ref.current) jogador2Ref.current.value = '';
                  } else {
                    alert('Por favor, insira os nomes dos dois jogadores.');
                  }
                }}
              >
                Adicionar Dupla
              </Button>
              <Button onClick={add8Duplas}>
                Adicionar 8 Duplas
              </Button>
            </div>
            <Button onClick={startTournament} disabled={duplas.length < 3}>
              Começar Torneio
            </Button>
          </>
        )}
        <section id="organogramaSection" className="section" style={{ display: faseAtual !== 'duplas' ? 'block' : 'none' }}>
          <h2>Organograma do Torneio</h2>
          <h3 id="faseAtual">
            {faseAtual === 'grupos' ? 'Fase de Grupos' :
              faseAtual === 'eliminatória' ? 'Fase Eliminatória' :
                'Torneio Finalizado'}
          </h3>
          {faseAtual === 'grupos' && (
            <FaseDeGrupos grupos={grupos} onAtualizarEstatisticas={atualizarEstatisticas} onUpdateGroups={setGrupos} />
          )}
          {faseAtual === 'eliminatória' && (
            <>
              <FaseEliminatoria
                fase="Fase Eliminatória"
                jogos={jogosEliminatoria}
                onAtualizarEstatisticas={atualizarEstatisticas}
              />
              {jogosEliminatoria.every(jogo => jogo.submetido) && (
                <React.Fragment>
                  <Button onClick={encerrarFaseEliminatoria}>Encerrar Fase Eliminatória</Button>
                  {finalConfigurada && (
                    <>
                      {jogosEliminatoria.find(jogo => jogo.fase === 'Final') && (
                        <p>Disputa Final: {jogosEliminatoria.find(jogo => jogo.fase === 'Final').dupla1.join(' e ')} vs {jogosEliminatoria.find(jogo => jogo.fase === 'Final').dupla2.join(' e ')}</p>
                      )}
                      {jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar') && (
                        <p>Disputa Pelo Terceiro: {jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar').dupla1.join(' e ')} vs {jogosEliminatoria.find(jogo => jogo.fase === 'Disputa 3º Lugar').dupla2.join(' e ')}</p>
                      )}
                    </>
                  )}
                  <Button onClick={finalizarTorneio}>
                    Finalizar Torneio
                  </Button>
                </React.Fragment>
              )}
            </>
          )}
          {historicoJogos.length > 0 && (
            <section className="section">
              <h3>Histórico de Jogos:</h3>
              {grupos.map(grupo => (
                <div key={grupo.id}>
                  <h4>Grupo {grupo.id + 1}</h4>
                  <ul>
                    {historicoJogos
                      .filter(jogo => jogo.grupo === grupo.id)
                      .map((jogo, index) => (
                        <li key={`${jogo.jogoIndex}-${index}`}>
                          Rodada {jogo.fase || 'Desconhecida'}:
                          {Array.isArray(jogo.dupla1) ? jogo.dupla1.join(' e ') : 'Dupla Desconhecida'}
                          vs
                          {Array.isArray(jogo.dupla2) ? jogo.dupla2.join(' e ') : 'Dupla Desconhecida'}
                          - Placar: {jogo.placar || 'Não Submetido'}
                        </li>
                      ))}
                  </ul>
                </div>
              ))}
            </section>
          )}
          {historicoFases.length > 0 && (
            <section className="section">
              <h3>Histórico de Fases:</h3>
              {historicoFases.map((fase, index) => (
                <div key={index}>
                  <h4>{fase.fase}</h4>
                  <p>Classificados:</p>
                  <ul>
                    {fase.classificados.map((dupla, i) => (
                      <li key={i}>{dupla.join(' e ')}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </section>
          )}
          {faseAtual === 'grupos' && (
            <Button onClick={encerrarFaseGrupos}>
              Encerrar Fase de Grupos
            </Button>
          )}
          {faseAtual === 'finalizado' && (
            <>
              <p>O torneio foi finalizado. Parabéns aos vencedores!</p>
              <h3>Ranking Final:</h3>
              <ul>
                {ranking.map((posição, index) => (
                  <li key={index}>
                    {posição.lugar}º Lugar:
                    {Array.isArray(posição.dupla) ? posição.dupla.join(' e ') : posição.dupla || 'Desconhecido'}
                  </li>
                ))}
              </ul>
            </>
          )}
        </section>
      </main>
      <footer>
        <p className="developed-by">Desenvolvido por Antônio Fichina Netto - 2024</p>
      </footer>
    </div>
  );

};

export default App;
