import React from 'react';
import downloadIcon from '../../assets/icons/download.svg'; // Caminho corrigido

function MeusTorneios({
  tournaments,
  onSelectTournament,
  onCreateNew,
  onDeleteTournament,
  onImportTournaments,
}) {
  const handleExport = () => {
    const data = JSON.stringify(tournaments, null, 2);
    const blob = new Blob([data], { type: 'application/json' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'torneios.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);
  };

  // ... resto do código ...

  return (
    <div className="meus-torneios">
      <h2>Meus Torneios</h2>
      <div className="button-container">
        <button className="criarTorneio" onClick={onCreateNew}>
          Criar Novo Torneio
        </button>
        <button className="exportarTorneio" onClick={handleExport}>
          <img src={downloadIcon} alt="Download Icon" className="download-icon" />
          Exportar Torneios
        </button>
        {/* ... resto do JSX ... */}
      </div>
      {/* ... resto do JSX ... */}
    </div>
  );
}

export default MeusTorneios;
