import React, { useState } from 'react';

function Jogo({ jogo, onAtualizarPlacar, onSubmeterPlacar }) {
  const [isSubmetido, setIsSubmetido] = useState(jogo.submetido);
  const [placar, setPlacar] = useState(jogo.placar);

  const handleSubmitScore = () => {
    if (placar) {
      onSubmeterPlacar(jogo, placar);
      setIsSubmetido(true);
    } else {
      alert('Por favor, insira um placar antes de submeter.');
    }
  };

  const handleEditScore = () => {
    setIsSubmetido(false);
  };

  return (
    <div>
      <p>{jogo.dupla1.join(' & ')} vs {jogo.dupla2.join(' & ')}</p>
      {isSubmetido ? (
        <React.Fragment>
          <span>{placar}</span>
          <button onClick={handleEditScore}>Editar</button>
        </React.Fragment>
      ) : (
        <React.Fragment>
          <input 
            value={placar}
            onChange={(e) => setPlacar(e.target.value)}
            placeholder="Placar"
          />
          <button onClick={handleSubmitScore}>Submeter Placar</button>
        </React.Fragment>
      )}
    </div>
  );
}

export default Jogo;
