import React from 'react';
import Input from '../UI/Input';
import Button from '../UI/Button';
import DuplasItem from './DuplasItem';

const DuplasList = ({ duplas, onAddDupla, onRemoveDupla }) => {
  const [jogador1, setJogador1] = React.useState('');
  const [jogador2, setJogador2] = React.useState('');

  const handleAddDupla = () => {
    if (jogador1 && jogador2) {
      onAddDupla([jogador1.toUpperCase(), jogador2.toUpperCase()]);
      setJogador1('');
      setJogador2('');
    }
  };

  return (
    <div className="duplas-list">
      <Input label="Jogador 1" value={jogador1} onChange={(e) => setJogador1(e.target.value)} />
      <Input label="Jogador 2" value={jogador2} onChange={(e) => setJogador2(e.target.value)} />
      <Button onClick={handleAddDupla}>Adicionar Dupla</Button>
      <ul>
        {duplas.map((dupla, index) => (
          <DuplasItem
            key={`${dupla[0]}-${dupla[1]}`}  // Usando os nomes dos jogadores para criar uma chave única
            dupla={dupla}
            index={index}
            onRemove={() => onRemoveDupla(index)}
          />
        ))}
      </ul>
    </div>
  );
};

export default DuplasList;
