import React, { useState, useEffect, useMemo } from 'react';
import Jogo from './Jogo';

const FaseEliminatoria = ({ fase, jogos, onAtualizarEstatisticas }) => {
  const [jogosState, setJogosState] = useState(jogos);
  const [ultimaRodadaConcluida, setUltimaRodadaConcluida] = useState(0);

  useEffect(() => {
    setJogosState(jogos);
    const maxRodada = Math.max(...jogos.map(jogo => jogo.rodada));
    setUltimaRodadaConcluida(prevRodada => Math.min(maxRodada, prevRodada));
  }, [jogos]);

  const jogosPorRodada = useMemo(() => {
    const result = {};
    jogosState.forEach(jogo => {
      if (!result[jogo.rodada]) {
        result[jogo.rodada] = [];
      }
      result[jogo.rodada].push(jogo);
    });
    return result;
  }, [jogosState]);

  useEffect(() => {
    const novaUltimaRodada = Object.keys(jogosPorRodada).reduce((max, rodada) =>
      jogosPorRodada[rodada].every(jogo => jogo.submetido) ? Math.max(max, parseInt(rodada)) : max,
      ultimaRodadaConcluida
    );
    setUltimaRodadaConcluida(novaUltimaRodada);
  }, [jogosState, jogosPorRodada, ultimaRodadaConcluida]);

  const handleSubmitScore = (placar, jogoIndex) => {
    setJogosState(prevJogos => {
      const newJogos = prevJogos.map(jogo =>
        jogo.jogoIndex === jogoIndex ? { ...jogo, placar: placar, submetido: true } : jogo
      );

      const rodadaAtual = newJogos.find(j => j.jogoIndex === jogoIndex).rodada;
      if (newJogos.filter(j => j.rodada === rodadaAtual).every(j => j.submetido)) {
        setUltimaRodadaConcluida(rodadaAtual);
      }

      return newJogos;
    });

    onAtualizarEstatisticas({ jogoIndex, placar, rodada: jogosState.find(j => j.jogoIndex === jogoIndex).rodada });
  };

  return (
    <div>
      <h2>{fase}</h2>
      {Object.keys(jogosPorRodada).sort((a, b) => a - b).map(rodada => {
        if (parseInt(rodada) <= ultimaRodadaConcluida + 1) {
          return (
            <div key={rodada}>
              <h3>
                {rodada === '1' ? 'Primeira Rodada' :
                  rodada === (Object.keys(jogosPorRodada).length).toString() ? 'Final' :
                    rodada === (Object.keys(jogosPorRodada).length - 1).toString() ? 'Semifinal' :
                      rodada === '2' ? 'Quartas de Final' : // '2' se aplicar ao seu torneio
                        `Rodada ${rodada}`}
              </h3>
              {jogosPorRodada[rodada].map(jogo => (
                <Jogo
                  key={jogo.jogoIndex}
                  jogo={jogo}
                  onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex)}
                />
              ))}
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default FaseEliminatoria;
