import React, { useEffect, useState } from 'react';
import Jogo from './Jogo';

function FaseFinal({ jogos, onAtualizarPlacar, onFinalizarTorneio }) {
  const [jogosSubmetidos, setJogosSubmetidos] = useState(jogos);

  // Sincroniza o estado local com as props quando elas mudam
  useEffect(() => {
    setJogosSubmetidos(jogos);
  }, [jogos]);

  const handleSubmitScore = (jogo, placar) => {
    if (typeof onAtualizarPlacar === 'function') {
      onAtualizarPlacar(jogo, placar);

      // Atualiza o estado local com o novo placar
      const updatedJogos = jogosSubmetidos.map((j) =>
        j === jogo ? { ...j, placar, submetido: true } : j
      );
      setJogosSubmetidos(updatedJogos);

      // Só chama onFinalizarTorneio se todos os jogos estiverem submetidos
      if (updatedJogos.every((j) => j.submetido) && typeof onFinalizarTorneio === 'function') {
        onFinalizarTorneio();
      }
    } else {
      console.error('onAtualizarPlacar não é uma função');
    }
  };

  return (
    <div>
      <h2>Fase Final</h2>
      {jogosSubmetidos.map((jogo, index) => (
        <div key={index}>
          <h3>{jogo.fase}</h3>
          <Jogo 
            jogo={jogo} 
            onSubmeterPlacar={handleSubmitScore}
          />
        </div>
      ))}
    </div>
  );
}

export default FaseFinal;
