// src/components/Duplas/DuplasList.js
import React, { useState } from 'react';

function DuplasList({ duplas, onAddDupla, onRemoveDupla }) {
  const [newDupla, setNewDupla] = useState(['', '']);

  const handleAddDupla = () => {
    if (newDupla[0] && newDupla[1]) {
      onAddDupla(newDupla);
      setNewDupla(['', '']);
    }
  };

  return (
    <div>
      <ul>
        {duplas.map((dupla, index) => (
          <li key={index}>{dupla.join(' & ')} <button onClick={() => onRemoveDupla(index)}>Remover</button></li>
        ))}
      </ul>
      <input value={newDupla[0]} onChange={(e) => setNewDupla([e.target.value, newDupla[1]])} placeholder="Jogador 1" />
      <input value={newDupla[1]} onChange={(e) => setNewDupla([newDupla[0], e.target.value])} placeholder="Jogador 2" />
      <button className="adicionar-dupla" onClick={addDupla}>Adicionar Dupla</button>
    </div>
  );
}

export default DuplasList;
