        }
        console.log('Hist√≥rico ap√≥s Eliminat√≥ria (jogo):', novoHistorico);
        return novoHistorico;
      });

      return updatedJogos;
    });
  };

  const iniciarFinal = (jogos) => {
    const semifinalGames = jogos.filter(jogo => jogo.fase === 'Semifinal');
