import React from 'react';
import { loadTournaments } from '../../utils/torneioUtils';

function MeusTorneios({ onSelectTournament, onCreateNew, onDeleteTournament }) {
  const tournaments = loadTournaments();

  const handleDelete = (nome) => {
    if (window.confirm(`Tem certeza que deseja excluir "${nome}"?`)) {
      onDeleteTournament(nome); // Chama a função passada pelo App.js
    }
  };

  return (
    <div className="meus-torneios">
      <h2>Meus Torneios</h2>
      <button className='CreateTourn' onClick={onCreateNew}>Criar Novo Torneio</button>
      {Object.keys(tournaments).length === 0 ? (
        <p>Nenhum torneio salvo ainda.</p>
      ) : (
        <ul>
          {Object.entries(tournaments).map(([nome, data]) => (
            <li key={nome}>
              <span>
                {nome} ({data.status}) - Criado em{' '}
                {new Date(data.dataCriacao).toLocaleDateString()}
              </span>
              <button onClick={() => onSelectTournament(nome)}>Abrir</button>
              <button onClick={() => handleDelete(nome)}>Excluir</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MeusTorneios;
