import React from 'react';

function FaseDeGrupos({ grupos, onUpdateGroups }) {
  const handleSubmitScore = (grupoId, jogoIndex, placar) => {
    const newGrupos = grupos.map(grupo => {
      if (grupo.id === grupoId) {
        return {
          ...grupo,
          jogos: grupo.jogos.map((jogo, index) => 
            index === jogoIndex ? { ...jogo, placar, submetido: true } : jogo
          )
        };
      }
      return grupo;
    });
    onUpdateGroups(newGrupos);
  };

  return (
    <div>
      {grupos.map(grupo => (
        <div key={grupo.id}>
          <h3>Grupo {grupo.id + 1}</h3>
          {grupo.jogos.map((jogo, index) => (
            <div key={index}>
              {jogo.dupla1.join(' & ')} vs {jogo.dupla2.join(' & ')} - 
              {jogo.submetido ? jogo.placar : 
                <input 
                  placeholder="Placar" 
                  onChange={(e) => handleSubmitScore(grupo.id, index, e.target.value)}
                />
              }
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default FaseDeGrupos;
