import React, { useState, useEffect, useMemo } from 'react';
import Jogo from './Jogo';

const FaseEliminatoria = ({ fase, jogos, onAtualizarEstatisticas }) => {
  const [jogosState, setJogosState] = useState(jogos);
  const [ultimaRodadaConcluida, setUltimaRodadaConcluida] = useState(0);

  // Atualiza jogosState quando os jogos passados como props mudam
  useEffect(() => {
    console.log('Jogos atualizados em FaseEliminatoria:', jogos);
    setJogosState(jogos);
    // Determina a última rodada concluída baseada nos jogos atuais
    const maxRodada = Math.max(...jogos.map(jogo => jogo.rodada));
    setUltimaRodadaConcluida(prevRodada => Math.min(maxRodada, prevRodada));
  }, [jogos]);

  // Monitora mudanças em jogosState para depuração
  useEffect(() => {
    console.log('Novo estado de jogos em FaseEliminatoria:', jogosState);
  }, [jogosState]);

  // Organiza jogos por rodada
  const jogosPorRodada = useMemo(() => {
    const result = {};
    jogosState.forEach(jogo => {
      if (!result[jogo.rodada]) {
        result[jogo.rodada] = [];
      }
      result[jogo.rodada].push(jogo);
    });
    return result;
  }, [jogosState]);

  // Atualiza ultimaRodadaConcluida quando todos os jogos de uma rodada são submetidos
  useEffect(() => {
    const novaUltimaRodada = Object.keys(jogosPorRodada).reduce((max, rodada) => {
      if (jogosPorRodada[rodada].every(jogo => jogo.submetido)) {
        return Math.max(max, parseInt(rodada));
      }
      return max;
    }, ultimaRodadaConcluida);
    setUltimaRodadaConcluida(novaUltimaRodada);
  }, [jogosState, jogosPorRodada]);

  // Função para lidar com a submissão do placar de um jogo
  const handleSubmitScore = (placar, jogoIndex) => {
    setJogosState(prevJogos => {
      const updatedJogos = prevJogos.map(jogo =>
        jogo.jogoIndex === jogoIndex ? { ...jogo, placar: placar, submetido: true } : jogo
      );

      const rodadaAtual = updatedJogos.find(j => j.jogoIndex === jogoIndex).rodada;
      if (updatedJogos.filter(j => j.rodada === rodadaAtual).every(j => j.submetido)) {
        setUltimaRodadaConcluida(rodadaAtual);
      }

      return updatedJogos;
    });

    // Notifica o componente pai sobre a atualização do placar
    onAtualizarEstatisticas({ jogoIndex, placar, rodada: jogosState.find(j => j.jogoIndex === jogoIndex).rodada });
  };

  return (
    <div>
      <h2>{fase}</h2>
      {Object.keys(jogosPorRodada).sort((a, b) => a - b).map(rodada => {
        // Renderiza apenas as rodadas até a próxima rodada incompleta
        if (parseInt(rodada) <= ultimaRodadaConcluida + 1) {
          return (
            <div key={rodada}>
              <h3>
                {rodada === '1' ? 'Primeira Rodada' :
                  rodada === Math.max(...Object.keys(jogosPorRodada)).toString() ? 'Final' :
                    rodada === (Math.max(...Object.keys(jogosPorRodada)) - 1).toString() ? 'Semifinal' :
                      rodada === '2' ? 'Quartas de Final' :
                        `Rodada ${rodada}`}
              </h3>
              {jogosPorRodada[rodada].map(jogo => (
                <Jogo
                  key={jogo.jogoIndex}
                  jogo={jogo}
                  onSubmitScore={(placar) => handleSubmitScore(placar, jogo.jogoIndex)}
                />
              ))}
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default FaseEliminatoria;
